I think this can be simplified with `GetIntArg` which returns a `std::optional`:

<details>
<summary>git diff</summary>

```diff
diff --git a/src/chainparams.cpp b/src/chainparams.cpp
index f41d2aaf8..02d51039e 100644
--- a/src/chainparams.cpp
+++ b/src/chainparams.cpp
@@ -27,19 +27,14 @@ void ReadSigNetArgs(const ArgsManager& args, CChainParams::SigNetOptions& option
         }
         options.challenge.emplace(ParseHex(signet_challenge[0]));
     }
-    if (args.IsArgSet("-signetblocktime")) {
-        const auto signetblocktime = args.GetArgs("-signetblocktime");
-        if (signetblocktime.size() != 1) {
-            throw std::runtime_error(strprintf("%s: -signetblocktime cannot be multiple values.", __func__));
-        }
+    if (const auto signetblocktime{args.GetIntArg("-signetblocktime")}) {
         if (!args.IsArgSet("-signetchallenge")) {
             throw std::runtime_error(strprintf("%s: -signetblocktime cannot be set without -signetchallenge", __func__));
         }
-        int64_t block_time;
-        if (!ParseInt64(signetblocktime[0], &block_time) || block_time <= 0) {
+        if (*signetblocktime <= 0) {
             throw std::runtime_error(strprintf("%s: -signetblocktime must be greater than 0", __func__));
         }
-        options.nPowTargetSpacing = block_time;
+        options.nPowTargetSpacing = signetblocktime;
     }
 }
 
</details>

