+TxEntry::TxEntryRef TxGraphCluster::GetLastTransaction()
+{
+    assert(m_tx_count > 0);
+    for (auto chunkit = m_chunks.rbegin(); chunkit != m_chunks.rend(); ++chunkit) {
+        if (!chunkit->txs.empty()) return chunkit->txs.back();
+    }
+    // Unreachable
+    assert(false);
+}
