+        to_add.Reset(first);
+        do {
+            S old = ret;
+            for (auto add : to_add) {
+                ret |= Descendants(add);
+                ret |= Ancestors(add);
+            }
+            ret &= left;
+            to_add = ret - old;
+        } while (to_add.Any());
+        return ret;
+    }
