+    /** Convert x>=0 to 2x (even), x<0 to -2x-1 (odd). */
+    static uint64_t SignedToUnsigned(int64_t x) noexcept
+    {
+        if (x < 0) {
+            return 2 * uint64_t(-(x + 1)) + 1;
+        } else {
