+    /** Convert even x to x/2 (>=0), odd x to -(x/2)-1 (<0). */
+    static int64_t UnsignedToSigned(uint64_t x) noexcept
+    {
+        if (x & 1) {
+            return -int64_t(x / 2) - 1;
+        } else {
