+template<typename SetType>
+SetType GetReducedParents(const DepGraph<SetType>& depgraph, ClusterIndex i) noexcept
+{
+    SetType ret = depgraph.Ancestors(i);
+    ret.Reset(i);
+    for (auto a : ret) {
+        if (ret[a]) {
+            ret -= depgraph.Ancestors(a);
+            ret.Set(a);
+        }
+    }
+    return ret;
