+SetType GetReducedChildren(const DepGraph<SetType>& depgraph, ClusterIndex i) noexcept
+{
+    SetType ret = depgraph.Descendants(i);
+    ret.Reset(i);
+    for (auto a : ret) {
+        if (ret[a]) {
+            ret -= depgraph.Descendants(a);
