+        if (!m_notifications) {
+            m_notifications = std::make_unique<KernelNotifications>(kernel_NotificationInterfaceCallbacks{
+                nullptr, nullptr, nullptr, nullptr, nullptr, nullptr, nullptr, nullptr});
+        }
+
+        if (!kernel::SanityChecks(*m_context)) {
