+{
+    if (!chainman) return;
+
+    {
+        LOCK(chainman->m_chainman->GetMutex());
+        for (Chainstate* chainstate : chainman->m_chainman->GetAll()) {
+            if (chainstate->CanFlushToDisk()) {
+                chainstate->ForceFlushStateToDisk();
+                chainstate->ResetCoinsViews();
+            }
+        }
+    }
+
+    delete chainman;
+    chainman = nullptr;
+}
