                SetType old_descendant_union = descendant_union;
                for (auto descendant: descendant_union) {
                    descendant_union |= children[descendant];
                }
                // exit when no new descendant was added
                if (old_descendant_union == descendant_union) break;
            }
            assert(descendant_union == depgraph.Descendants(i));
