bool GetPrivKey(int pos, const SigningProvider& arg, CKey& key) const override
{
    return m_xonly ? arg.GetKeyByXOnly(XOnlyPubKey(m_pubkey), key) : 
                     arg.GetKey(m_pubkey.GetID(), key);
}
