     }
 
     for (nlmsghdr* hdr = (nlmsghdr*)response; NLMSG_OK(hdr, recv_result); hdr = NLMSG_NEXT(hdr, recv_result)) {
+        // Netlink multipart dumps include control messages; ignore them.
+        if (hdr->nlmsg_type == NLMSG_DONE) {
+            break; // end of dump
+        }
+        if (hdr->nlmsg_type == NLMSG_ERROR) {
+            continue; // optional: parse nlmsgerr if desired
+        }
+        if (hdr->nlmsg_type != RTM_NEWROUTE) {
+            continue; // not a route message, don't treat payload as rtmsg
+        }
+
         rtmsg* r = (rtmsg*)NLMSG_DATA(hdr);
         int remaining_len = RTM_PAYLOAD(hdr);
 
