     std::vector key_in{m_rng.randbytes<std::byte>(Obfuscation::KEY_SIZE)};
     DataStream ds_in;
     ds_in << key_in;
-    BOOST_CHECK_EQUAL(ds_in.size(), 1 + Obfuscation::KEY_SIZE); // serialized as a vector
