     ds_in >> obfuscation;
 
     // Test saving the key.
     std::vector<std::byte> key_out;
     DataStream ds_out;
     ds_out << obfuscation;
+    const DataStream obfuscation_bytes{ds_out};
     ds_out >> key_out;
 
-    // Make sure saved key is the same.
+    // Make sure key is unchanged, and that key and Obfuscation representations are the same.
     BOOST_CHECK_EQUAL_COLLECTIONS(key_in.begin(), key_in.end(), key_out.begin(), key_out.end());
+    BOOST_CHECK_EQUAL_COLLECTIONS(key_bytes.begin(), key_bytes.end(), obfuscation_bytes.begin(), obfuscation_bytes.end());
 }
 
